import 'regenerator-runtime/runtime';
import React, { Component, useCallback, useEffect, useState } from 'react'
import PropTypes from 'prop-types'
import logo from './assets/logo.svg';
import nearlogo from './assets/gray_near_logo.svg';
import near from './assets/near.svg';
import * as nearlib from 'near-api-js';
import Container from '@material-ui/core/Container';
import Typography from '@material-ui/core/Typography';
import Box from '@material-ui/core/Box';
import Link from '@material-ui/core/Link';
import { makeStyles } from '@material-ui/core/styles';
import Paper from '@material-ui/core/Paper';
import Grid from '@material-ui/core/Grid';


import './App.css';
import Signup from './Signup.js';
import Search from './Search.js';

class App extends Component {

  constructor(props) {
    super(props);
    this.state = {
      login: false,
      speech: null,
      balance: null,
      messages: null,

    }
    this.signedInFlow = this.signedInFlow.bind(this);
    this.requestSignIn = this.requestSignIn.bind(this);
    this.requestSignOut = this.requestSignOut.bind(this);
    this.signedOutFlow = this.signedOutFlow.bind(this);
    this.changeGreeting = this.changeGreeting.bind(this);
    this.getMessages = this.getMessages.bind(this);
  }

  componentDidMount() {

    let loggedIn = this.props.wallet.isSignedIn();


    if (loggedIn) {
      this.signedInFlow();

    } else {
      this.signedOutFlow();
    }
  }

  componentWillUnmount() {

  }

  async signedInFlow() {

    this.setState({
      login: true,
    })

    const accountId = await this.props.wallet.getAccountId()

    this.setState({balance: (await this.props.wallet.account().state()).amount});




    //console.log(balance)
    if (window.location.search.includes("account_id")) {
      window.location.replace(window.location.origin + window.location.pathname)
    }
    await this.welcome();
    await this.getMessages();

  }

  async getMessages() {

    this.setState({messages: await this.props.contract.getMessages()});

  }

  async welcome() {

    const response = await this.props.contract.welcome({ account_id: accountId });

    this.setState({speech: response.text});

  }

  async requestSignIn() {

    const appTitle = 'Validator Tools';

    await this.props.wallet.requestSignIn(
      window.nearConfig.contractName,
      appTitle
    )
  }

  requestSignOut() {

    this.props.wallet.signOut();

    setTimeout(this.signedOutFlow, 500);
  }

  async changeGreeting() {

    await this.props.contract.setGreeting({ message: 'howdy' });

    await this.welcome();
  }

  signedOutFlow() {

    if (window.location.search.includes("account_id")) {
      window.location.replace(window.location.origin + window.location.pathname)
    }

    this.setState({
      login: false,
      speech: null
    })

  }

  render() {

    const self = this;

    const useStyles = makeStyles((theme) => ({
      root: {
        flexGrow: 1,
      },
      validators: {
        'min-height': 250,
        textAlign: 'left',
      },
      paper: {
        padding: theme.spacing(3),
        textAlign: 'center',
        color: theme.palette.text.secondary,
      },
    }));

    function CenteredGrid() {

      const classes = useStyles();

      return (
        <div className={classes.root}>
          <Grid container spacing={0} alignItems="flex-start" alignContent="flex-start">

            <Grid item xs={6}>
                <Typography variant="h4" component="h1">
                  BlazeNet - NEAR Validator Tools
                 </Typography>
            </Grid>

            <Grid item xs={6}>
              <Paper className={classes.paper}>
                <div className="">
                  <div>
                  <img className="logo" src={nearlogo} alt="NEAR logo" />
                    {self.state.login ? 
                      <div>
                      @{accountId}&nbsp;
                      <button onClick={self.requestSignOut}>Log out</button>
                      </div>
                      : <button onClick={self.requestSignIn}>Log in with NEAR</button>}
                  </div>
                </div>
              </Paper>
            </Grid>

            <Grid item className={classes.validators} xs={6}>
              <Typography variant="h4" component="h1">
                NEAR VALIDATORS
              </Typography>
              <Search wallet={self.props.wallet} />
            </Grid>
            <Grid item xs={6}>
                  <Signup wallet={self.props.wallet} contract={self.props.contract} balance={self.state.balance} />
            </Grid>

            <Grid item  xs={12}>

            </Grid>

            <Grid item xs={3}>
              <Paper className={classes.paper}>xs=3</Paper>
            </Grid>
            <Grid item xs={3}>
              <Paper className={classes.paper}>xs=3</Paper>
            </Grid>
            <Grid item xs={3}>
              <Paper className={classes.paper}>xs=3</Paper>
            </Grid>
            <Grid item xs={3}>
              <Paper className={classes.paper}>xs=3</Paper>
            </Grid>
          </Grid>
        </div>
      );
    }

    return (

      <Container>
        <Box my={4}>
          <CenteredGrid />
        </Box>
      </Container>
      )
    }
  }

  App.propTypes = {
    contract: PropTypes.shape({
      addMessage: PropTypes.func.isRequired,
      getMessages: PropTypes.func.isRequired
    }).isRequired,
    wallet: PropTypes.shape({
      requestSignIn: PropTypes.func.isRequired,
      signOut: PropTypes.func.isRequired
    }).isRequired
  }

  export default App;
